import React from 'react';
//import { Card, Button } from 'react-bootstrap';
import img from '../images/welcomeImage.png';
import logo from '../images/logo1.png';
import zpImg from '../images/zp-img.png';
import zpLogo from '../images/zippr-logo.png';
import { Button } from 'react-bootstrap';
import $ from 'jquery';
let ddnArr=[];
let ddnStr=[];

export default class Home extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      toggleSource:"ddn" ,
      ddnAddress:"",
      zpAddress:""
    }
    this.inputFieldChange=this.inputFieldChange.bind(this); 
    this.inputValues=this.inputValues.bind(this);
    this.toggleDDN=this.toggleDDN.bind(this); 
    this.toggleZP=this.toggleZP.bind(this);
    this.zpInputChange=this.zpInputChange.bind(this); 
    this.ddnInputValue=this.ddnInputValue.bind(this); 
    this.searchSubmit=this.searchSubmit.bind(this);
}

inputFieldChange(e){
  let value=e.target.value;
console.log(e.target.value);
var foo = value.split("-").join(""); // remove hyphens
  if (foo.length > 0) {
    foo = foo.match(new RegExp('.{1,4}', 'g')).join("-");
  }
  console.log(foo);
  this.setState({
    inputText:foo
  })
}
componentDidMount(){
  for (let x in this.refs) {
    this.refs[x].onkeypress = (e) => 
      this.inputValues(e, this.refs[x]);
  }
}

inputValues(e, field){
  console.log(e.target.maxLength,"tarrrrrrrrrrrr........");
  let length=e.target.maxLength;
  let value=e.target.value;
  if(value.length==length){
    console.log(field,"++");
    let next = this.refs[field.name].nextSibling;

      if (next && next.tagName === "INPUT") {
        this.refs[field.name].nextSibling.focus();
      }
  }
}
toggleDDN(e){
  this.setState({
    toggleSource:"ddn"
  })
console.log(this.state.toggleSource)
} 
toggleZP(e){
  this.setState({
    toggleSource:"zp" 
  })
  }
  zpInputChange(e){
    e.preventDefault();
