    ddnArr.map((val)=>{
      if(Object.keys(val)[0]==e.target.name){
        let index = ddnArr.indexOf(val);
        if (index > -1) {
          ddnArr.splice(index, 1);
        }
      }
    })
    ddnArr.push(ddnObj);
    if(ddnArr.length==4){
      /*
    let a=[];
    ddnArr.map((ele)=>{
      a.push(ele["ip1"]);
      a.push(ele["ip2"]);
      a.push(ele["ip3"]);
      a.push(ele["ip4"]);
    }) */
