import React from 'react';
//import { Card, Button } from 'react-bootstrap';
import img from '../images/welcomeImage.png';
import logo from '../images/logo1.png';
import { Button } from 'react-bootstrap';

export default class Home extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      inputText:""
    }
    this.inputFieldChange=this.inputFieldChange.bind(this);
}

inputFieldChange(e){
  let value=e.target.value;
console.log(e.target.value);
var foo = value.split("-").join(""); // remove hyphens
  if (foo.length > 0) {
    foo = foo.match(new RegExp('.{1,4}', 'g')).join("-");
  }
  console.log(foo);

  this.setState({
    inputText:foo
  })
}





render(){

    return(
<div className='container-fluid'>
<div className='row'>
<div className='col-md-12'>
<img className='welcome-image' src={img} alt="image1" />
<img className='logo1' src={logo} alt="company-logo" />

<div className='col-md-12 data-fields'>
<h4 className='search-head'>What do you want to search?</h4>
<Button variant="light" size="sm" class="ddn-button" style={{border:"1px solid", "text-align":"center","background-color":"white", "border-radius": "40px"}}>DIGITAL DOOR NUMBER</Button> &nbsp;
<Button variant="light" size="sm" class="zp-button" style={{border:"1px solid", "background-color":"white", "border-radius": "40px"}}>Zippr Code</Button>  <br /><br />


<input type="text" class="inputText-home" onChange={this.inputFieldChange} placeholder="APGR-WPN-271-567" value={this.state.inputText}  /> <button><i class="fa fa-search" /></button>

<div class="input-group">
    <input type="text" class="form-control" placeholder="Search this blog" />
    <div class="input-group-append">
      <button class="btn btn-secondary" type="button">
        <i class="fa fa-search"></i>
      </button>
    </div>
  </div>

</div>

<div className='col-md-3 rect1'>

</div>

<div className='col-md-9'>


</div>
</div>
</div>


</div>
        )
}

